configuration.slidingWindow:
(
    select
        /**
         * If reach end of string (concat(SQLi+NULL)) then concat nullifies the result
         */
        concat(
            'SQLi',
            substr(
                (${INJECTION}),
                ${INDEX},
                /**
                 * Minus 'SQLi' should apply
                 */
                ${CAPACITY}
            )
        )
)
