schema.table:
select group_concat(
    '%04'
    ||name
    ||'%050%04','${SEPARATOR_CELL_SQL}'
)||'${TRAIL_SQL}'
from (
    select * 
    from sqlite_master 
    WHERE type='table'
    ORDER BY tbl_name
    {limit}
)

row.query:
select ifnull(
    group_concat(
        '%04'
        ||r
        ||'%050%04',
        '${SEPARATOR_CELL_SQL}'
    ),
'')||'${TRAIL_SQL}'
from (
    select distinct ${FIELDS} r 
    from ${TABLE}
    {limit}
)
