schema.table:
select group_concat(
    '${ENCLOSE_VALUE_SQL}'
    ||name
    ||'${SEPARATOR_QTE_SQL}0${ENCLOSE_VALUE_SQL}','${SEPARATOR_CELL_SQL}'
)||'${TRAIL_SQL}'
from (
    select * 
    from sqlite_master 
    WHERE type='table'
    ORDER BY tbl_name
    {limit}
)

row.query:
select ifnull(
    group_concat(
        '${ENCLOSE_VALUE_SQL}'
        ||r
        ||'${SEPARATOR_QTE_SQL}0${ENCLOSE_VALUE_SQL}',
        '${SEPARATOR_CELL_SQL}'
    ),
'')||'${TRAIL_SQL}'
from (
    select distinct ${FIELDS} r 
    from ${TABLE}
    {limit}
)
